const trace_opts = {
    name: "Language",
    type: "bar",
    hoverinfo: "text",
    marker: {
        color: 'rgba(5,5,30,0.7)',
        line: {color: 'rgba(5,5,30,1.0)', width: 1}
    }
}
const layout_opts = {
    font: {size: 16, family: `"Menlo", "Monaco", "Lucida Console", "Liberation Mono", "DejaVu Sans Mono", "Bitstream Vera Sans Mono", "Courier New", serif`},
}
var data_all = {};

function get_traces(data_raw, group_key, x_key, y_key) {
    data = {};
    var groups = [...new Set(data_raw[group_key])];
    groups.forEach(group => {
        indices = data_raw[group_key].map(
            (el, index) => (el === group) ? index : NaN
        ).filter(
            el => !isNaN(el)
        )
        data[group] = [{
            x: indices.map(i => data_raw[x_key][i]),
            y: indices.map(i => data_raw[y_key][i]),
            hovertext: indices.map(i => `${labels[data_raw[x_key][i]]}: ${data_raw[y_key][i]}`),
            ...trace_opts
        }]
    });
    return {groups: groups, data: data}
}

function get_map_traces(data_raw, group_key, x_key, y_key) {
    var data_fix = {};
    var groups_fix = [];
    var {groups, data} = get_traces(data_raw, group_key, x_key, y_key);
    groups.forEach(group => {
        var lang_data = structuredClone(data[group]);
        lang_data[0].type = 'choropleth';
        lang_data[0].colorscale = 'YlGnBu',
        lang_data[0].reversescale = true,
        lang_data[0].z = lang_data[0].y;
        lang_data[0].locations = lang_data[0].x;
        lang_data[0].text = lang_data[0].x;
        lang_data[0].colorbar = {title: {text: "% of sites", font: {size: 16}}};
        lang_data[0].hovertext = lang_data[0].locations.map((el, i) => `${el}: ${lang_data[0].z[i]}%`),
        data_fix[group] = lang_data;
        groups_fix.push(group)
        // var group_log = `${group}_log`;
        // var lang_data_log = structuredClone(lang_data);
        // lang_data_log[0].z = lang_data_log[0].z.map(el => Math.log(el+1))
        // lang_data_log[0].colorbar = {title: {text: "Log % of sites", font: {size: 16}}};
        // data_fix[group_log] = lang_data_log;
        // groups_fix.push(group_log);
    })
    return {groups: groups_fix, data: data_fix}
}

// lang stats
fetch("q1_top_langs.json")
    .then(res => res.json())
    .then(function (data_raw) {
        data = [{
            x: data_raw["lang"],
            y: data_raw["perc_sites"],
            hovertext: data_raw["lang"].map((el, i) => `${labels[el]}: ${data_raw["perc_sites"][i]}%`),
            ...trace_opts
        }]
        var layout = {
            ...layout_opts,
            title: {text: 'Top languages', font: {size: 16}},
            xaxis: {title: 'Language', titlefont: {size: 16}},
            yaxis: {title: '% of websites', titlefont: {size: 16}},
        }
        Plotly.newPlot("top-langs", data, layout);
    })
    .catch(function(err) {
        console.error(err)
    });

// tld-lang stats
fetch("q2_tld_top_langs.json")
    .then(res => res.json())
    .then(function (data_raw) {
        var plot_id = "tld-top-langs";
        var default_group = "com";
        var {groups, data} = get_traces(data_raw, "tld", "lang", "perc_of_tld");
        data_all[plot_id] = data;
        var plot = document.getElementById('tld-top-langs');
        var layout = {
            ...layout_opts,
            title: {text: 'Top languages for .com', font: {size: 16}},
            xaxis: {title: 'Language', titlefont: {size: 16}},
            yaxis: {title: '% of websites', titlefont: {size: 16}},
        };
        Plotly.newPlot(plot, data[default_group], layout);
        var dropdown = document.getElementById(`${plot_id}-select`);
        groups.forEach(group => {
            var opt = document.createElement('option')
            opt.value = group
            opt.innerText = group
            if (group === default_group) {opt.selected = "selected"}
            dropdown.appendChild(opt)
        })
        dropdown.onchange = function (event) {
            layout.title.text = `Top languages for .${event.target.value}`;
            Plotly.newPlot(plot_id, data_all[plot_id][event.target.value], layout);
        }
    })
    .catch(function(err) {
        console.error(err)
    });

